import { Query } from 'react-apollo';
import gql from 'graphql-tag';

export const TEST_QUERY = gql`
  query testQuery {
    posts {
      edges {
        node {
          title
        }
      }
    }
  }
`;

const Index = props => (
  <div>
    <Query query={TEST_QUERY}>
      {({ data }) => {
        console.log(props);
        return (
          <div>
            {data.posts.edges.map(({ node }) => {
              return <p>{node.title}</p>;
            })}
          </div>
        );
      }}
    </Query>
  </div>
);

export default Index;
